// src/store/projects.js
import { defineStore } from 'pinia';
import axios from 'axios';

export const useProjectStore = defineStore('projects', {
  state: () => ({
    projects: [],
    currentProject: null,
    currentUser: 'userID_atual'
  }),
  actions: {
    async fetchProjects() {
      try {
        const response = await axios.get('http://localhost:3000/api/projects');
        this.projects = response.data;
      } catch (error) {
        console.error('Error fetching projects:', error);
      }
    },
    async addProject(project) {
      if (this.isUserAuthorized(project.creatorId)) {
        try {
          const response = await axios.post('http://localhost:3000/api/projects', project);
          this.projects.push(response.data); // Adicione o projeto retornado pelo backend ao state
        } catch (error) {
          console.error('Error adding project:', error);
        }
      }
    },
    isUserAuthorized(creatorId) {
      return this.currentUser === creatorId;
    }
  }
});
